import React, { useRef } from 'react'

const Ex04 = () => {
    /*
    (1) 사용자가 input 태그 안에 입력한 값을 가져오기
    (2) 그 값에 따라서 해당하는 경로로 이미지의 src 속성 변경하기
    */
    const textRef = useRef();
    const imgRef = useRef();

    const imgList = [
        {name : '고래', src : 'data:image/jpeg;base64,'},
        {name : '돌고래', src : 'https://image.dongascience.com/Photo/2020/06/d7cdc8c0033067f76a66eec382a540e0.jpg'},
        {name : '범고래', src : 'https://upload.wikimedia.org/wikipedia/commons/3/37/Killerwhales_jumping.jpg'},
        {name : '혹등고래', src : 'https://upload.wikimedia.org/wikipedia/commons/a/a3/Humpback_Whales_-_Flickr_-_Christopher.Michel_%2838%29.jpg'},
        {name : '향유고래', src : 'https://i.namu.wiki/i/XvGNYfeQdDE2vV9jUz5Wf1vYst-xAs48bayFuPY5l2nvtdAumzaOXEzyqWgdbQ7QDYvG62c5uvbYhs8s3Dx7Hw.webp'},
        {name : '상괭이', src : 'data:image/jpeg;base64,'},
        {name : '아마존강돌고래', src : 'data:image/jpeg;base64,'},
        {name : '일각고래', src : 'data:image/jpeg;base64,'},
        {name : '밍크고래', src : 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQACNtGQ_5erSPReif7lfPiGtvWUGX3h-pfvQ&usqp=CAU'}
    ]
    const imgChange = ()=>{
        // console.log(textRef.current.value);
        // switch(textRef.current.value){
        //     case imgList[0].name : imgRef.current.src = imgList[0].src; break;
        //     case imgList[1].name : imgRef.current.src = imgList[1].src; break;
        //     case imgList[2].name : imgRef.current.src = imgList[2].src; break;
        //     case imgList[3].name : imgRef.current.src = imgList[3].src; break;
        //     case imgList[4].name : imgRef.current.src = imgList[4].src; break;
        //     case imgList[5].name : imgRef.current.src = imgList[5].src; break;
        //     case imgList[6].name : imgRef.current.src = imgList[6].src; break;
        //     case imgList[7].name : imgRef.current.src = imgList[7].src; break;
        //     case imgList[8].name : imgRef.current.src = imgList[8].src; break;
        // }
        imgList.forEach(item=>{
            textRef.current.value===item.name && (imgRef.current.src=item.src);
        })
        
        // input 입력값 비워주기
        textRef.current.value = '';

        // input 오토포커싱
        textRef.current.focus();
    }
  return (
    <div>
        <p>어떤 고래 사진을 원하시나요?</p>
        <input type="text" ref={textRef}/>
        <button onClick={imgChange}>변경</button>
        <br/><br/>
        <img src={imgList[0].src}
        width="250px" alt="" ref={imgRef}/>
    </div>
  )
}

export default Ex04